import { useRouteError } from "react-router-dom";
import PageContent from "../components/PageContent";

function ErrorPage(){
    const error=useRouteError();
    title="Error occured";
    message="something went wrong";
    if(error.status===500){
        message="Something"
    }
    return <PageContent title={title}>
        <p>{message}</p>
    </PageContent>
}
export default ErrorPage;
